function dmlPadrao ($query) {
    global $DB;
    if ($DB['TYPE'] == ZBX_DB_POSTGRESQL) {
        $query = str_replace('varchar', 'character varying', $query);
        $query = str_replace('int', 'integer', $query);
        $query = str_replace('`', '', $query);
    }
    return $query;
}

